var rect = {
    perimeter: function (x,y) {
        return (2*(x+y))
    },

    area: function (x,y) {
        return (x*y);
    }

};

function solveRect(l,w) {
    console.log("Solving for rectangle with length " + l + " and width = " + w);

    if(l < 0 || w < 0) {
        console.log("Rectangle dimensions should be greater than zero");
    }
    else{
        console.log("The area of the rectangle is " + rect.area(l,w));
        console.log("The perimeter of the rectangle is " + rect.perimeter(l,w));
        console.log()
    }
}

solveRect(2,4);
solveRect(3,5);
solveRect(-3,5);

